cv::Point2f recursive_bezier(const std::vector&lt;cv::Point2f&gt;&amp; control_points, float t){    // TODO: Implement de Casteljau's algorithm    if (control_points.size() == 1)        return control_points[0];    else    {        std::vector&lt;cv::Point2f&gt; points;        for (auto i = 0; i &lt; control_points.size() - 1; i += 1)        {            auto &amp;p0 = control_points[i];            auto &amp;p1 = control_points[i + 1];            points.emplace_back((1 - t) * p0 + t * p1);        }        return recursive_bezier(points, t);    }}
void bezier(const std::vector&lt;cv::Point2f&gt;&amp; control_points, cv::Mat&amp; window){    // TODO: Iterate through all t = 0 to t = 1 with small steps, and call de Casteljau's     // recursive Bezier algorithm.    for (double t = 0.0; t &lt;= 1.0; t += 0.001)    {        auto point = recursive_bezier(control_points, t);        window.at&lt;cv::Vec3b&gt;(point.y, point.x)[2] = 255;    }}
