from PIL import Imageimport numpy as npimport matplotlib.pyplot as pltimport cv2from skimage.io import imreadfrom scipy.fftpack import ifftn, fft2, ifft2import matplotlib.pyplot as pltimport matplotlib.cm as cmimport cmathimport warnings%matplotlib inlinewarnings.filterwarnings(action='ignore')
def fliproi(img,ranges):    roi=img[ranges[0]:ranges[1], ranges[2]:ranges[3]]    roi=np.flip(roi,1)    roi=np.flip(roi,0)    img[ranges[0]:ranges[1], ranges[2]:ranges[3]]=roi        img=cv2.imread('./imgs/lena.png', cv2.IMREAD_GRAYSCALE)def getShiftedFreqSpectrum(img):    fimg=fft2(img, shape=img.shape, axes=tuple((0, 1)))    fimgamp=np.abs(fimg)    fimgphase=np.angle(fimg)    roilen=img.shape[0]//2    for y in [0,1]:        for x in [0, 1]:            ranges=[y*roilen, (y+1)*roilen,x*roilen,(x+1)*roilen]            fliproi(fimgamp,ranges)            fliproi(fimgphase,ranges)    return fimg, fimgamp, fimgphasedef getFreqSpectrum(img):    fimg=fft2(img, shape=img.shape, axes=tuple((0, 1)))    fimgamp=np.abs(fimg)    fimgphase=np.angle(fimg)    return fimg, fimgamp, fimgphasefimg,fimgamp, fimgphase=getShiftedFreqSpectrum(img)
plt.figure(figsize=(20,20))plt.subplot(1,3,1)plt.imshow(img, cmap="gray")plt.title("Orignal Image")plt.subplot(1,3,2)plt.imshow(np.log(fimgamp), cmap="gray")plt.title("Frequency Spectrum Amplification (Zero centered)")plt.subplot(1,3,3)plt.imshow(np.abs(fimgphase), cmap="gray")plt.title("Frequency Spectrum Phase Angle (Zero centered)")plt.show()




