static bool insideTriangle(int x, int y, const Vector3f* _v){    // TODO : Implement this function to check if the point (x, y) is inside the triangle represented by _v[0], _v[1], _v[2]    Eigen::Vector2f side1;    side1 &lt;&lt; _v[1].x() - _v[0].x(), _v[1].y() - _v[0].y();    Eigen::Vector2f side2;    side2 &lt;&lt; _v[2].x() - _v[1].x(), _v[2].y() - _v[1].y();    Eigen::Vector2f side3;    side3 &lt;&lt; _v[0].x() - _v[2].x(), _v[0].y() - _v[2].y();        Eigen::Vector2f v1;    v1 &lt;&lt; x - _v[0].x(), y - _v[0].y();    Eigen::Vector2f v2;    v2 &lt;&lt; x - _v[1].x(), y - _v[1].y();    Eigen::Vector2f v3;    v3 &lt;&lt; x - _v[2].x(), y - _v[2].y();        float z1 = side1.x() * v1.y() - side1.y() * v1.x();    float z2 = side2.x() * v2.y() - side2.y() * v2.x();    float z3 = side3.x() * v3.y() - side3.y() * v3.x();        if ((z1 &gt; 0 &amp;&amp; z2 &gt; 0 &amp;&amp; z3 &gt; 0) || (z1 &lt; 0 &amp;&amp; z2 &lt; 0 &amp;&amp; z3 &lt; 0))    {        return true;    }    else    {        return false;    }}
