static std::tuple&lt;float, float, float&gt; computeBarycentric2D(float x, float y, const Vector3f* v){    float xp = x, yp = y;    float xa = v[0].x(), ya = v[0].y();    float xb = v[1].x(), yb = v[1].y();    float xc = v[2].x(), yc = v[2].y();    float gamma = ((xb - xa) * (yp - ya) - (xp - xa) * (yb - ya)) /                   ((xb - xa) * (yc - ya) - (xc - xa) * (yb - ya));    float beta = (xp - xa - gamma * (xc - xa)) / (xb - xa);    float alpha = 1.0f - beta - gamma;    return {alpha, beta, gamma};}



