//Screen space rasterizationvoid rst::rasterizer::rasterize_triangle(const Triangle&amp; t) {    auto v = t.toVector4();        // TODO : Find out the bounding box of current triangle.    // iterate through the pixel and find if the current pixel is inside the triangle    auto minX = std::min(v[0].x(), std::min(v[1].x(), v[2].x()));    auto minY = std::min(v[0].y(), std::min(v[1].y(), v[2].y()));    auto maxX = std::max(v[0].x(), std::max(v[1].x(), v[2].x()));    auto maxY = std::max(v[0].y(), std::max(v[1].y(), v[2].y()));    // If so, use the following code to get the interpolated z value.    // TODO : set the current pixel (use the set_pixel function) to the color of the triangle (use getColor function) if it should be painted.    for (int i = minX; i &lt;= maxX; i++) {        for (int j = minY; j &lt;= maxY; j++) {            if (insideTriangle(i + 0.5f, j + 0.5f, t.v)) {                float minDepth = FLT_MAX;                auto tup = computeBarycentric2D(i, j, t.v);                                float alpha, beta, gamma;                std::tie(alpha, beta, gamma) = tup;                float wReciprocal = 1.0 / (alpha / v[0].w() + beta / v[1].w() + gamma / v[2].w());                float zInterpolated = alpha * v[0].z() / v[0].w() + beta * v[1].z() / v[1].w() + gamma * v[2].z() / v[2].w();                zInterpolated *= wReciprocal;                minDepth = std::min(minDepth, zInterpolated);                auto index = get_index(i, j);                if (depth_buf[index] &gt; minDepth) {                    Eigen::Vector3f color = t.getColor();                    Vector3f point;                    point &lt;&lt; i, j, minDepth;                    depth_buf[index] = minDepth;                    set_pixel(point, color);                }            }        }    }}

